        [TestCase(DayOneInput, 280)]
        public void PartOne(string input, int expected)
        {
            var result = FindFloor(input);

            Assert.That(result.Floor, Is.EqualTo(expected));
        }

        [TestCase(")", 1)]
        [TestCase("()())", 5)]
        [TestCase(DayOneInput, 1797)]
        public void PartTwo(string input, int expected)
        {
            var result = FindFloor(input);

            Assert.That(result.Position, Is.EqualTo(expected));
        }

        private Result FindFloor(string input)
        {
            int floor = 0;
            int position = 0;
            int enteredBasementAt = 0;
            foreach (var letter in input)
            {
                if (letter.Equals('('))
                {
                    floor++;
                }
                else
                {
                    floor--;
                }

                position++;

                if (floor < 0 && enteredBasementAt == 0)
                {
                    enteredBasementAt = position;
                }
            }

            return new Result
            {
                Floor = floor,
                Position = enteredBasementAt,
            };
        }
    }

    public class Result
    {
        public int Floor { get; set; }
        public int Position { get; set; }
    }
